import React from 'react';

export default function PopupWithForm({isOpen, onClose, name, title, buttonText, children}) {
  React.useEffect(() => {
    if (!isOpen) return;
    function clickClose(evt) {
      if (
        evt.target.classList.contains('popup') &&
        !evt.target.classList.contains('popup__container')
      ) {
        onClose();
      }
    }

    function closeByEsc(evt) {
      if (evt.key === 'Escape') {
        onClose();
      }
    }

    document.addEventListener('click', clickClose);
    document.addEventListener('keydown', closeByEsc);

    return () => {
      document.removeEventListener('click', clickClose);
      document.removeEventListener('keydown', closeByEsc);
    };
  }, [isOpen]);

  return (
    <section className={`popup popup_type_${name} ${isOpen}`}>
      <div className='popup__container'>
        <button
          type='button'
          aria-label='Закрыть'
          className='popup__close-button'
          onClick={onClose}
        ></button>
        <h2 className='popup__title'>{title}</h2>
        <form name={`${name}-form`} className={`form ${name}-form`}>
          {children}
          <button
            type='submit'
            className='form__save-button'
            name={`${name}-form-save-button`}
          >
            {buttonText}
          </button>
        </form>
      </div>
    </section>
  );
}
