import { useLocation } from 'react-router-dom';
import React, { useState } from 'react';
import logo from '../../images/logo.svg';

export default function Header({ isLogged, userData}) {
  const location = useLocation();

  const [email, setEmail] = useState('email');
  const [navElement, setNavElement] = useState('');
  const [navElementLink, setNavElementLink] = useState('#');

  React.useEffect(() => {
    if (location.pathname === '/sign-up') {
      setEmail('')
      setNavElement('Войти')
      setNavElementLink('/sign-in')
    } else if (location.pathname === '/sign-in') {
      setEmail('')
      setNavElement('Регистрация')
      setNavElementLink('/sign-up')
    } else if (isLogged){
      setEmail('email')
      setNavElement('Выйти')
    }

  }, [location]);

  return (
    <header className='header'>
      <div className='header__container'>
        <img className='header__logo' src={logo} alt='лого' />
        <div>
          <span className='header__nav'>{email}</span>
          <a className='header__nav' href={navElementLink}>{navElement}</a>
        </div>
      </div>
    </header>
  );
}
