import React, { useState } from 'react';
import logo from '../../images/logo.svg';

export default function Header({ isLogged, userData}) {
  const location = useLocation();
  const [firstLink, setFirstLink] = useState('first');
  const [secondLink, setSecondLink] = useState('second');

  React.useEffect(() => {
    if (location.pathname === '/sign-up') {
      setFirstLink('rega')
      setSecondLink()



    }

  }, [location]);

  return (
    <header className='header'>
      <div className='header__container'>
        <img className='header__logo' src={logo} alt='лого' />
        <div>
          <a className='header__nav'>{firstLink}</a>
          <a className='header__nav'>{secondLink}</a>
        </div>
      </div>
    </header>
  );
}
