import React, { useState } from 'react';
import Header from './Header/Header';
import Main from './Main/Main';
import Footer from './Footer/Footer';
import PopupWithForm from './PopupWithForm/PopupWithForm';
import ImagePopup from './ImagePopup/ImagePopup';
import { api } from '../utils/Api';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function App() {
  const [selectedCard, setSelectedCard] = React.useState(null);

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);

  const isOpen =
    isEditAvatarPopupOpen ||
    isEditProfilePopupOpen ||
    isAddPlacePopupOpen ||
    selectedCard;

  React.useEffect(() => {
    function clickClose(evt) {
      if (
        evt.target.classList.contains('popup') &&
        !evt.target.classList.contains('popup__container')
      ) {
        closeAllPopups();
      }
    }

    function closeByEsc(evt) {
      if (evt.key === 'Escape') {
        closeAllPopups();
      }
    }
    if (isOpen) {
      document.addEventListener('click', clickClose);
      document.addEventListener('keydown', closeByEsc);

      return () => {
        document.removeEventListener('click', clickClose);
        document.removeEventListener('keydown', closeByEsc);
      };
    }
  }, [isOpen]);

  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard(null);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  const [currentUser, setCurrenUser] = useState(null);

  React.useEffect(() => {
    api
      .getUserInfo()
      .then((res) => {
        setCurrenUser(res);
      })
      .catch((err) => {
        console.log(`Catch ${err}`);
      });
  }, []);

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    // Отправляем запрос в API и получаем обновлённые данные карточки
    {card && api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
      setSelectedCard((state) => state.map((c) => (c._id === card._id ? newCard : c)));
    });}
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <Header />
      {currentUser && (
        <Main
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
          onCardClick={setSelectedCard}
          handleCardLike={handleCardLike}
          onCardLike={handleCardLike}
        />
      )}
      <Footer />
      <ImagePopup card={selectedCard} onClose={closeAllPopups} />
      <PopupWithForm
        onClose={closeAllPopups}
        isOpen={isEditProfilePopupOpen ? 'popup_opened' : ''}
        name='profile-edit'
        title='Редактировать профиль'
        buttonText='Сохранить'
      >
        <>
          <input
            required
            minLength='2'
            maxLength='40'
            placeholder='Имя'
            type='text'
            className='form__input form__input_profile-name'
            name='profile-form-name'
          />
          <span className='profile-form-name-error form__error'></span>
          <input
            required
            minLength='2'
            maxLength='200'
            placeholder='О себе'
            type='text'
            className='form__input form__input_profile-job'
            name='profile-form-job'
          />
          <span className='profile-form-job-error form__error'></span>
        </>
      </PopupWithForm>

      <PopupWithForm
        onClose={closeAllPopups}
        isOpen={isAddPlacePopupOpen ? 'popup_opened' : ''}
        name='add-card'
        title='Новое место'
        buttonText='Создать'
      >
        <>
          <input
            required
            minLength='2'
            maxLength='30'
            type='text'
            placeholder='Название'
            className='form__input form__input_card-name'
            name='input-card-name'
          />
          <span className='input-card-name-error form__error'></span>
          <input
            required
            type='url'
            placeholder='Ссылка на картинку'
            className='form__input form__input_card-link'
            name='input-card-link'
          />
          <span className='input-card-link-error form__error'></span>
        </>
      </PopupWithForm>

      <PopupWithForm
        onClose={closeAllPopups}
        isOpen={isEditAvatarPopupOpen ? 'popup_opened' : ''}
        name='avatar-change'
        title='Обновить аватар'
        buttonText='Сохранить'
      >
        <>
          <input
            required
            type='url'
            placeholder='Ссылка на картинку'
            className='form__input form__input_avatar-link'
            name='avatar-edit-input'
          />
          <span className='avatar-edit-input-error form__error'></span>
        </>
      </PopupWithForm>

      <PopupWithForm
        onClose={closeAllPopups}
        name='delete'
        title='Вы уверены?'
        buttonText='Да'
      />
    </CurrentUserContext.Provider>
  );
}

export default App;
