export default function ImagePopup({name, link}) {
  return (
    <section className="popup popup_type_lightbox">
    <div className="popup__image-container">
      <button
        type="button"
        aria-label="Закрыть"
        className="popup__close-button"
      ></button>
      <img className="popup__lightbox-image" src={link} alt={name} />
      <p className="popup__image-description"></p>
    </div>
  </section>
  );
}
