import React, { useState } from 'react';
import Header from './Header/Header';
import Main from './Main/Main';
import Footer from './Footer/Footer';
import PopupWithForm from './PopupWithForm/PopupWithForm';
import EditProfilePopup from './EditProfilePopup/EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup/EditAvatarPopup';
import ImagePopup from './ImagePopup/ImagePopup';
import { api } from '../utils/Api';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function App() {
  const [selectedCard, setSelectedCard] = React.useState(null);

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);
  const [cards, setCards] = React.useState([]);

  const isOpen =
    isEditAvatarPopupOpen ||
    isEditProfilePopupOpen ||
    isAddPlacePopupOpen ||
    selectedCard;

  React.useEffect(() => {
    function clickClose(evt) {
      if (
        evt.target.classList.contains('popup') &&
        !evt.target.classList.contains('popup__container')
      ) {
        closeAllPopups();
      }
    }

    function closeByEsc(evt) {
      if (evt.key === 'Escape') {
        closeAllPopups();
      }
    }
    if (isOpen) {
      document.addEventListener('click', clickClose);
      document.addEventListener('keydown', closeByEsc);

      return () => {
        document.removeEventListener('click', clickClose);
        document.removeEventListener('keydown', closeByEsc);
      };
    }
  }, [isOpen]);

  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard(null);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  const [currentUser, setCurrenUser] = useState({});

  React.useEffect(() => {
    api
      .getUserInfo()
      .then((res) => {
        setCurrenUser(res);
      })
      .catch((err) => {
        console.log(`Catch ${err}`);
      });
  }, []);

  React.useEffect(() => {
    api
      .getCards()
      .then((res) => {
        setCards(res);
      })
      .catch((err) => {
        console.log(`Catch ${err}`);
      });
  }, []);

  function handleCardLike(card) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
      setCards((cards) => cards.map((c) => (c._id === card._id ? newCard : c)));
    });
  }

  function handleCardDelete(card) {
    api.deleteCard(card._id).then(() => {
      setCards((cards) => cards.filter((c) => c._id != card._id));
    });
  }

  function handleUpdateUser({ name, about }) {
    api
      .setUserInfo(name, about)
      .then((res) => setCurrenUser(res))
      .then(() => closeAllPopups());
  }

  function handleUpdateAvatar(obj) {
    api.changeAvatar(obj.avatar).then((res) => setCurrenUser(res)).then(() => { closeAllPopups() })
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <Header />
      {currentUser && (
        <Main
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
          onCardClick={setSelectedCard}
          handleCardLike={handleCardLike}
          onCardDelete={handleCardDelete}
          onCardLike={handleCardLike}
          cards={cards}
        />
      )}
      <Footer />
      <EditAvatarPopup
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
        onUpdateAvatar={handleUpdateAvatar}
      />
      <ImagePopup card={selectedCard} onClose={closeAllPopups} />
      <EditProfilePopup
        onClose={closeAllPopups}
        isOpen={isEditProfilePopupOpen}
        onUpdateUser={handleUpdateUser}
      />
      <PopupWithForm
        onClose={closeAllPopups}
        isOpen={isAddPlacePopupOpen ? 'popup_opened' : ''}
        name='add-card'
        title='Новое место'
        buttonText='Создать'
      >
        <>
          <input
            required
            minLength='2'
            maxLength='30'
            type='text'
            placeholder='Название'
            className='form__input form__input_card-name'
            name='input-card-name'
          />
          <span className='input-card-name-error form__error'></span>
          <input
            required
            type='url'
            placeholder='Ссылка на картинку'
            className='form__input form__input_card-link'
            name='input-card-link'
          />
          <span className='input-card-link-error form__error'></span>
        </>
      </PopupWithForm>

      <PopupWithForm
        onClose={closeAllPopups}
        name='delete'
        title='Вы уверены?'
        buttonText='Да'
      />
    </CurrentUserContext.Provider>
  );
}

export default App;
