import React from 'react';

export default function ImagePopup({isOpen, onClose, card}) {
  React.useEffect(() => {
    if (!isOpen) return;
    function clickClose(evt) {
      if (
        evt.target.classList.contains('popup') &&
        !evt.target.classList.contains('popup__container')
      ) {
        onClose();
      }
    }

    function closeByEsc(evt) {
      if (evt.key === 'Escape') {
        onClose();
      }
    }

    document.addEventListener('click', clickClose);
    document.addEventListener('keydown', closeByEsc);

    return () => {
      document.removeEventListener('click', clickClose);
      document.removeEventListener('keydown', closeByEsc);
    };
  }, [isOpen]);

  return (
    <section
      className={`popup popup_type_lightbox ${
        card ? 'popup_opened' : ''
      }`}
    >
      <div className='popup__image-container'>
        <button
          type='button'
          aria-label='Закрыть'
          className='popup__close-button'
          onClick={onClose}
        ></button>
        <img
          className='popup__lightbox-image'
          src={card ? card.link : '#'}
          alt={card ? card.name : 'null'}
        />
        <p className='popup__image-description'>
          {card ? card.name : 'null'}
        </p>
      </div>
    </section>
  );
}
