import React from 'react';
import PopupWithForm from '../PopupWithForm/PopupWithForm';
import { CurrentUserContext } from '../../contexts/CurrentUserContext';

export default function EditProfilePopup({
  isOpen,
  onClose,
}) {
const [name, setName] = React.useState('')
const [description, setDescription] = React.useState('')
const currentUser = React.useContext(CurrentUserContext);

React.useEffect(() => {
  setName(currentUser.name);
  setDescription(currentUser.about);
}, [currentUser]);

function handleChange(e) {
  setValue(e.target.value);
}

  return (
    <PopupWithForm
      onClose={onClose}
      isOpen={isOpen ? 'popup_opened' : ''}
      name='profile-edit'
      title='Редактировать профиль'
      buttonText='Сохранить'
    >
      <>
        <input
          required
          minLength='2'
          maxLength='40'
          placeholder='Имя'
          type='text'
          className='form__input form__input_profile-name'
          name='profile-form-name'
          value={name}
          onChange={handleChange}
        />
        <span className='profile-form-name-error form__error'></span>
        <input
          required
          minLength='2'
          maxLength='200'
          placeholder='О себе'
          type='text'
          className='form__input form__input_profile-job'
          name='profile-form-job'
          value={description}
          onChange={handleChange}
        />
        <span className='profile-form-job-error form__error'></span>
      </>
    </PopupWithForm>
  );
}
