import React, { useState } from 'react';
import { api } from '../../utils/Api';
import { CurrentUserContext } from '../../contexts/CurrentUserContext';
import { Routes, Route, useNavigate } from 'react-router-dom';

export default function Register({}) {
  return (
    <section
      className='register'
    >
        <h2 className='popup__title'>Регистрация</h2>
        <form
          name='register-form'
          className='form register-form'
          onSubmit={onSubmit}
        >
          <input
            required
            minLength='2'
            maxLength='40'
            placeholder='Email'
            type='text'
            className='form__input form__input_profile-name'
            name='profile-form-name'
            value={name || ''}
            onChange={handleNameChange}
          />
          <span className='profile-form-name-error form__error'></span>
          <input
            required
            minLength='2'
            maxLength='200'
            placeholder='Пароль'
            type='text'
            className='form__input form__input_profile-job'
            name='profile-form-job'
            value={description || ''}
            onChange={handleDescriptionChange}
          />
          <span className='profile-form-job-error form__error'></span>
          <button
            type='submit'
            className='form__save-button'
            name={`${name}-form-save-button`}
          >
            {buttonText}
          </button>
        </form>
    </section>
  );
}
