export default function ImagePopup(props) {

  React.useEffect(() => {

    function clickClose(evt) {
      if (evt.target.classList.contains('popup') && !evt.target.classList.contains('popup__container')) {
        props.onClose()
      }
    }

    function closeByEsc(evt) {
      if (evt.key === 'Escape') {
        props.onClose()
      }
    }

    document.addEventListener('click', clickClose)
    document.addEventListener('keydown', closeByEsc)

    return () => {
      document.removeEventListener('click', clickClose)
      document.removeEventListener('keydown', closeByEsc)
    };

  }, [props.isOpen])

  return (
    <section className="popup popup_type_lightbox">
    <div className="popup__image-container">
      <button
        type="button"
        aria-label="Закрыть"
        className="popup__close-button"
      ></button>
      <img className="popup__lightbox-image" />
      <p className="popup__image-description"></p>
    </div>
  </section>
  );
}
