import React from "react"

export default function ImagePopup(props) {

  React.useEffect(() => {

    function clickClose(evt) {
      if (evt.target.classList.contains('popup') && !evt.target.classList.contains('popup__container')) {
        props.onClose()
      }
    }

    function closeByEsc(evt) {
      if (evt.key === 'Escape') {
        props.onClose()
      }
    }

    document.addEventListener('click', clickClose)
    document.addEventListener('keydown', closeByEsc)

    return () => {
      document.removeEventListener('click', clickClose)
      document.removeEventListener('keydown', closeByEsc)
    };

  }, [props.isOpen])

  return (
    <section className={`popup popup_type_lightbox ${props.card ? 'popup_opened' : ''}`}>
    <div className="popup__image-container">
      <button
        type="button"
        aria-label="Закрыть"
        className="popup__close-button"
        onClick={props.onClose}
      ></button>
      <img className="popup__lightbox-image" src={props.card ? props.card.link : '#'} alt={props.card ? props.card.name : 'null'} />
      <p className="popup__image-description">{props.card ? props.card.name : 'null'}</p>
    </div>
  </section>
  );
}
