import React from "react";
import { api } from "../../utils/Api";
import Card from "../Card/Card";

export default function Main(props) {

  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
      api.getCards()
      .then((res)=>{
      setCards(res)
    })
  }, [])

  const [userName, setUserName] = React.useState()
  const [userDescription, setUserDescription] = React.useState()
  const [userAvatar, setUserInfoserAvatar] = React.useState()

  React.useEffect(() => {
    api.getUserInfo().then((res) => {
      setUserName(res.name)
      setUserDescription(res.about)
      setUserInfoserAvatar(res.avatar)
    })
  }, [])

  return (
    <main>
      <section className="profile">
        <div className="profile__avatar-container">
        <button className="profile__overlay" onClick={() => props.onEditAvatar()}></button>
          <img
            src={userAvatar}
            className="profile__avatar"
            alt="Аватар"
          />
        </div>
        <div className="profile__info">
          <h1 className="profile__name">{userName}</h1>
          <button
            className="profile__edit-button"
            aria-label="Редактировать"
            type="button"
            onClick={() => props.onEditProfile()}
          ></button>
          <p className="profile__description">{userDescription}</p>
        </div>
        <button
          className="profile__add-button"
          aria-label="Добавить место"
          type="button"
          onClick={() => props.onAddPlace()}
        ></button>
      </section>
      <section className="elements">
        {cards.map(item => (
          <Card key={item._id} props={item} onCardClick={props.onCardClick} />
        ))}
      </section>
    </main>
  );
}
