import React from "react";
export default function PopupWithForm(props) {

  React.useEffect(() => {

    function clickClose(evt) {
      if (evt.target.classList.contains('popup') && !evt.target.classList.contains('popup__container')) {
        props.onClose()
      }
    }

    function closeByEsc(evt) {
      if (evt.key === 'Escape') {
        props.onClose()
      }
    }

    document.addEventListener('click', clickClose)
    document.addEventListener('keydown', closeByEsc)

    return () => {
      document.removeEventListener('click', clickClose)
      document.removeEventListener('keydown', closeByEsc)
    };

  }, [props.isOpen])

  return (
    <section className={`popup popup_type_${props.name} ${props.isOpen}`} >
    <div className="popup__container">
      <button
        type="button"
        aria-label="Закрыть"
        className="popup__close-button"
        onClick={props.onClose}
      ></button>
      <h2 className="popup__title">{props.title}</h2>
      <form name={props.name} className="edit-form add-form">
        {props.children}
      </form>
    </div>
  </section>
  );
}
